create or replace view o_0 as select o_orderkey as x2, COUNT(*) as annot from orders_5 where o_orderdate < DATE '1996-12-31' and o_orderdate > DATE '1996-01-01' group by o_orderkey;
create or replace view l_0 as select l_returnflag as x1, l_orderkey as x2, l_partkey as x3, l_suppkey as x4, l_quantity * annot as l_quantity from lineitem_5, o_0 where l_orderkey = x2;
create or replace view p_0 as select p_partkey as x3, COUNT(*) as annot from part_5 where p_name LIKE '%blue%' group by p_partkey;
create or replace view ps_0 as select ps_partkey as x3, ps_suppkey as x4, ps_supplycost * annot as ps_supplycost from partsupp_5, p_0 where ps_partkey = x3;
create or replace view l_1 as select x1, x2, l_0.x4, l_quantity * ps_supplycost as part_cost from l_0, ps_0 where l_0.x3 = ps_0.x3 and l_0.x4 = ps_0.x4;
create or replace view l_2 as select x1, x2, x4, SUM(part_cost) as part_cost from l_1 group by x1, x2, x4;
create or replace view s_0 as select s_suppkey as x4, s_nationkey as x7 from supplier_5 where exists (select 1 from l_2 where s_suppkey = l_2.x4);
create or replace view s_1 as select x4, x7 from s_0 where exists (select 1 from nation_5 where s_0.x7 = n_nationkey);
create or replace view n_1 as select n_nationkey as x7, n_name as x8 from nation_5 where exists (select 1 from (select x7 from s_1 group by x7) st where st.x7 = n_nationkey);
create or replace view s_2 as select x4, x8, count(*) as annot from s_1 join n_1 using (x7) group by x4, x8;
select x1, x2, x8, SUM(part_cost*annot) as part_cost from s_2 join l_2 using (x4) group by x1, x2, x8;