create or replace view aggView1008929203505553687 as select id as v15, title as v28, production_year as v29 from title as t where production_year>2000;
create or replace view aggJoin5819418531336557080 as select movie_id as v15, info_type_id as v3, v28, v29 from movie_info_idx as mi_idx, aggView1008929203505553687 where mi_idx.movie_id=aggView1008929203505553687.v15;
create or replace view aggView3181104586581217108 as select id as v3 from info_type as it where info= 'bottom 10 rank';
create or replace view aggJoin6293055749193018482 as select v15, v28, v29 from aggJoin5819418531336557080 join aggView3181104586581217108 using(v3);
create or replace view aggView8888019820600282904 as select v15, MIN(v28) as v28, MIN(v29) as v29 from aggJoin6293055749193018482 group by v15;
create or replace view aggJoin1478071996000534761 as select company_type_id as v1, note as v9, v28, v29 from movie_companies as mc, aggView8888019820600282904 where mc.movie_id=aggView8888019820600282904.v15 and note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%';
create or replace view aggView588788735193796261 as select v1, MIN(v28) as v28, MIN(v29) as v29, MIN(v9) as v27 from aggJoin1478071996000534761 group by v1;
create or replace view aggJoin5566018611472515952 as select kind as v2, v28, v29, v27 from company_type as ct, aggView588788735193796261 where ct.id=aggView588788735193796261.v1 and kind= 'production companies';
create or replace view res as select MIN(v27) as v27, MIN(v28) as v28, MIN(v29) as v29 from aggJoin5566018611472515952;
