create or replace view bag1 as select c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice from orders, customer where c_custkey = o_custkey;
create or replace view l_agg as select l_orderkey, sum(l_quantity) as l_sum from lineitem where l_orderkey in (select v1_orderkey from q18_inner) group by l_orderkey;
create or replace view res as select c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice, sum(l_sum) as agg from bag1, l_agg where o_orderkey = l_orderkey group by c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice;
select sum(c_name), sum(c_custkey), sum(o_orderkey), sum(o_orderdate), sum(o_totalprice), sum(agg) from res;